mapper.select:
SELECT book.BookId, book.BookName, book.BookAuthor, book.CategoryId, book.BookPress, book.PublishedDate, book.BookEnterTime, book.BookIntroduction, book.BookAddress, book.BookPicPath, book.DelMaker, IFNULL(book_stock.BookNumber,0) AS BookNumber, category.CategoryName FROM book LEFT JOIN book_stock ON book.BookId = book_stock.BookId LEFT JOIN category ON book.CategoryId = category.CategoryId WHERE book.DelMaker = 0
AND book.BookName LIKE CONCAT('%',#{keyWord},'%')
AND book.BookEnterTime >= #{startTime}
AND book.BookEnterTime <= #{endTime}
AND category.CategoryName = #{category}
Limit #{pageSetVo.pageOffset},#{pageSetVo.pageValue}

SELECT COUNT(1) FROM book LEFT JOIN book_stock ON book.BookId = book_stock.BookId LEFT JOIN category ON book.CategoryId = category.CategoryId WHERE book.DelMaker = 0
AND book.BookName LIKE CONCAT('%',#{keyWord},'%')
AND book.BookEnterTime >= #{startTime}
AND book.BookEnterTime <= #{endTime}
AND category.CategoryName = #{category}
