mapper.select:
SELECT book.BookName, book.BookAuthor, book.BookPicPath, borrow.BorrowId, borrow.BorrowDate, borrow.BorrowDay, IFNULL(a.RenewDay,0) AS RenewDay FROM borrow LEFT JOIN book ON book.BookId = borrow.BookId LEFT JOIN category ON category.CategoryId = book.CategoryId LEFT JOIN (SELECT BorrowId,sum(RenewDay) AS RenewDay FROM book_renew GROUP BY BorrowId ) AS a ON borrow.BorrowId = a.BorrowId WHERE borrow.UserId = #{userid} AND borrow.IsReturn = #{isreturn}
AND book.BookName LIKE CONCAT('%',#{keyWord},'%')
AND book.BookEnterTime >= #{startTime}
AND book.BookEnterTime <= #{endTime}
AND category.CategoryName = #{category}
Limit #{pageSetVo.pageOffset},#{pageSetVo.pageValue}

SELECT 
    COUNT(1)
FROM borrow
LEFT JOIN book
ON book.BookId = borrow.BookId
LEFT JOIN category
ON category.CategoryId = book.CategoryId
LEFT JOIN 
(SELECT BorrowId,sum(RenewDay) AS RenewDay FROM book_renew GROUP BY BorrowId ) AS a
ON borrow.BorrowId = a.BorrowId 
WHERE borrow.UserId = #{userid} 
AND borrow.IsReturn = #{isreturn}

        AND book.BookName LIKE CONCAT('%',#{keyWord},'%')


    AND book.BookEnterTime  >=  #{startTime}


    AND book.BookEnterTime  <=  #{endTime}


    AND category.CategoryName = #{category}
